mapper.select:
select count(*) CalendarEmotion
  from (
  		select memberId, regDate 
  		from FreeDiary
  		where memberId = #{memberId} and regDate = #{regDate}
  	union all
  		select memberId, regDate 
  		from GratitudeDiary
  		where memberId = #{memberId} and regDate = #{regDate}
  ) CalendarEmotion

mapper.update:
update CalendarEmotion

	emotionId = #{emotionId}

where 1=1 
  and memberId=#{memberId}
  and regDate=#{regDate}
